import { GoChevronDown } from "react-icons/go";
import { GrEdit } from "react-icons/gr";
import { TiDeleteOutline } from "react-icons/ti";
import EditParty from "./EditParty";

function WaitingParty({ reference, onDelete }) {
  const iconProperties = "float-right text-2xl cursor-pointer ml-5 mr-5";

  const [showEdit, setShowEdit] = useState(false);

  let editWindow = null;
  if (showEdit) {
    editWindow = <EditParty />;
  }

  const handleDelete = () => {
    onDelete(reference.key);
  };

  return (
    <div>
      <div className="box-border bg-purple-300 m-5 p-5">
        <span className="italic">{reference.name}</span>
        <GoChevronDown className={iconProperties} />
        <GrEdit className={iconProperties} />
        <TiDeleteOutline className={iconProperties} onClick={handleDelete} />
      </div>
      <div>{showEdit && editWindow}</div>
    </div>
  );
}

export default WaitingParty;
