import { useState } from "react";
import CreateModal from "./CreateModal";
import Button from "./Button";

function CreateParty() {
  const [showModal, setShowModal] = useState(false);

  const[nameTerm, setNameTerm] = useState("Jake");
  const[sizeTerm, setSizeTerm] = useState(3);
  const[phoneTerm, setPhoneTerm] = useState("(774)343-4300");
  const[timeTerm, setTimeTerm] = useState("7:30 PM");

  const handleClick = () => {
    setShowModal(!showModal);
  };

  const handleSubmit = () => {
    console.log("Button pressed!")
  }

  const handleClose = () => {
    setShowModal(false);

    console.log(showModal);
  };

  const actionBar = (
    <div>
      <button className="bg-green-300 p-2" onClick={handleSubmit}>Add to Waitlist</button>
    </div>
  );

  const modal = (
    <CreateModal onClose={handleClose} actionBar={actionBar}>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          className="input ml-2 pl-2 mr-2"
          value={nameTerm}
          onChange={handleChange}
        />
        <input
          className="input ml-2 pl-2 mr-2"
          value={sizeTerm}
          onChange={handleChange}
        />
        <input
          className="input ml-2 pl-2 mr-2"
          value={phoneTerm}
          onChange={handleChange}
        />
        <input
          className="input ml-2 pl-2 mr-2"
          value={timeTerm}
          onChange={handleChange}
        />
        <button className="button bg-blue-300 p-1 pl-2 pr-2 is-primary">
          Save
        </button>
      </form>
    </CreateModal>
  );

  return (
    <div>
      <Button className="bg-green-300 p-2 m-5" onClick={handleClick}>
        Create Party
        {showModal && modal}
      </Button>
    </div>
  );
}

export default CreateParty;
